        private System.Drawing.Bitmap RemoveArcsSmart(Bitmap noBgBmp, int BoxSize, float Ratio)
        {
            if (BoxSize < 3 || BoxSize % 2 == 0)
                throw new System.ArgumentException("Parameter gerade.", "BoxSize");

            if (Ratio < 0 || Ratio > 1)
                throw new System.ArgumentException("Parameter not in range.", "Ratio");

            Bitmap noArcsBmp = new System.Drawing.Bitmap(noBgBmp);
            int toSub = (BoxSize - 1) / 2;
            int PixelCount;

            for (int y = toSub; y < noArcsBmp.Height - toSub; y++)
            {
                for (int x = toSub; x < noArcsBmp.Width - toSub; x++)
                {
                    System.Drawing.Color Pixel = noBgBmp.GetPixel(x, y);
                    if (Pixel.R == 128 && Pixel.G == 128 && Pixel.B == 255) // Arc Pixel
                    {
                        PixelCount = 0;

                        //Look at neighbors
                        for (int j = 0; j < BoxSize; j++)
                        {
                            for (int k = 0; k < BoxSize; k++)
                            {
                                System.Drawing.Color color = noBgBmp.GetPixel(x - toSub + k, y - toSub + j);

                                if ((color.R == 255 && color.G == 255 && color.B == 255) || (color.R == 128 && color.G == 128 && color.B == 255))
                                {
                                    continue;
                                }
                                else
                                {
                                    PixelCount++;
                                }
                            }
                        }

                        float PixelsAvailable = BoxSize ^ 2 - 1;

                        if (((float)PixelCount / PixelsAvailable) >= Ratio)
                        {
                            noArcsBmp.SetPixel(x, y, System.Drawing.Color.Black);
                        }
                    }
                }
            }

            return noArcsBmp;
        }

