        private void Binarise(ref Bitmap src)
        {
            for (int x = 0; x < src.Width; x++)
            {
                for (int y = 0; y < src.Height; y++)
                {
                    System.Drawing.Color Pixel = src.GetPixel(x, y);
                    if (Pixel.R == 255 && Pixel.G == 255 && Pixel.B == 255)
                    {
                        continue;
                    }
                    else
                    {
                        src.SetPixel(x, y, System.Drawing.Color.Black);
                    }
                }
            }
        }

        private System.Drawing.Bitmap VerticalHistogram(Bitmap BinarisedBmp)
        {
                System.Drawing.Bitmap HistogramBmp = new System.Drawing.Bitmap(BinarisedBmp.Width, BinarisedBmp.Height, BinarisedBmp.PixelFormat);
                this.Histogram = new int[BinarisedBmp.Width];

                for (int x = 0; x < BinarisedBmp.Width; x++)
                {
                    for (int y = 0; y < BinarisedBmp.Height; y++)
                    {
                        System.Drawing.Color Pixel = BinarisedBmp.GetPixel(x, y);
                        if (Pixel.R == 0 && Pixel.G == 0 && Pixel.B == 0)
                        {
                            Histogram[x]++;
                        }
                    }

                    if (Histogram[x] == 0)
                    {
                        HistogramBmp.SetPixel(x, HistogramBmp.Height-Histogram[x]-1, System.Drawing.Color.Black);
                    }
                    else
                    {
                        HistogramBmp.SetPixel(x, HistogramBmp.Height-Histogram[x], System.Drawing.Color.Black);
                    }
                }

                return HistogramBmp;
        }

        private System.Drawing.Bitmap SegmentLetters(Bitmap BinarisedBmp)
        {
            System.Drawing.Bitmap SegmentedBmp = new System.Drawing.Bitmap(BinarisedBmp);

            int x = 0;
            int start;
            int end;

            //for every character
            for (int j = 0; j < 4; j++)
            {
                start = FindLetterStart(SegmentedBmp, x);
                end = FindLetterEnd(SegmentedBmp, start);
                x = end;

                if (end - start > 35) //2 connected characters
                {
                    //do histogram segmentation
                    int xPos = HistogramSegmentation(SegmentedBmp, start, end);
                    this.LettersNotNormalized[j] = this.CropImageBig(BinarisedBmp, start, 0, xPos - start, BinarisedBmp.Height);
                    this.LettersNotNormalized[j + 1] = this.CropImageBig(BinarisedBmp, xPos, 0, end - xPos, BinarisedBmp.Height);

                    //DrawVerticalLine(ref SegmentedBmp, start+23, System.Drawing.Color.Blue);
                    //DrawVerticalLine(ref SegmentedBmp, end-15, System.Drawing.Color.Blue);
                    DrawVerticalRedLine(ref SegmentedBmp, start);
                    DrawVerticalRedLine(ref SegmentedBmp, end);
                    DrawVerticalLine(ref SegmentedBmp, xPos, System.Drawing.Color.Green);

                    j++;
                }
                else // 1 character
                {
                    this.LettersNotNormalized[j] = this.CropImageBig(BinarisedBmp, start, 0, end - start, BinarisedBmp.Height);

                    DrawVerticalRedLine(ref SegmentedBmp, start);
                    DrawVerticalRedLine(ref SegmentedBmp, end);
                }
            }

            return SegmentedBmp;
        }

        private int HistogramSegmentation(System.Drawing.Bitmap img, int start, int end)
        {
            int min = 999999;
            int xPos = 0;

            for (int x = start + 23; x < end-15; x++)
            {
                if(this.Histogram[x] < min)
                {
                    min = this.Histogram[x];
                    xPos = x;
                }
            }

            return xPos;
        }

        private int FindLetterStart(System.Drawing.Bitmap img, int k)
        {
            if (k < img.Width)
            {
                for (int x = k; x < img.Width; x++)
                {
                    for (int y = 0; y < img.Height; y++)
                    {
                        System.Drawing.Color Pixel = img.GetPixel(x, y);
                        if (Pixel.R == 0 && Pixel.G == 0 && Pixel.B == 0)
                        {
                            return x-1;
                        }
                    }
                }
            }

            return -1;
        }

        private int FindLetterEnd(System.Drawing.Bitmap img, int k)
        {

            if (k < img.Width)
            {
                for (int x = k+10; x < img.Width; x++)
                {
                    if (IsWhiteLine(img, x))
                    {
                        return x;
                    }
                }
            }

            return img.Width - 1;
        }

        private int FindLetterEndHistogram(System.Drawing.Bitmap img, int k)
        {
            k++;

            if (k < img.Width)
            {
                for (int x = k; x < img.Width; x++)
                {
                    if (IsWhiteLine(img, x))
                    {
                        return x;
                    }
                }
            }

            return img.Width - 1;
        }

        private int FindLetterStartH(System.Drawing.Bitmap img, int k)
        {
            if (k < img.Height)
            {
                for (int y = k; y < img.Height; y++)
                {
                    for (int x = 0; x < img.Width; x++)
                    {
                        System.Drawing.Color Pixel = img.GetPixel(x, y);
                        if (Pixel.R == 0 && Pixel.G == 0 && Pixel.B == 0)
                        {
                            return y - 1;
                        }
                    }
                }
            }

            return -1;
        }

        private int FindLetterEndH(System.Drawing.Bitmap img, int k)
        {

            if (k < img.Height)
            {
                for (int y = k+15; y < img.Height; y++)
                {
                    if (IsWhiteLineH(img, y))
                    {
                        return y;
                    }
                }
            }

            return img.Width - 1;
        }

