        private bool IsWhiteLine(System.Drawing.Bitmap img, int xPos)
        {
            bool IsWhite = true;

            for(int y=0;y<img.Height;y++)
            {
                System.Drawing.Color Pixel = img.GetPixel(xPos, y);

                if (Pixel.R == 0 && Pixel.G == 0 && Pixel.B == 0)
                {
                    IsWhite = false;
                }
            }

            return IsWhite;
        }

        private bool IsWhiteLineH(System.Drawing.Bitmap img, int xPos)
        {
            bool IsWhite = true;

            for (int x = 0; x < img.Width; x++)
            {
                System.Drawing.Color Pixel = img.GetPixel(x, xPos);

                if (Pixel.R == 0 && Pixel.G == 0 && Pixel.B == 0)
                {
                    IsWhite = false;
                }
            }

            return IsWhite;
        }

        private System.Drawing.Bitmap CropImage(System.Drawing.Bitmap src, int x, int y, int width, int height)
        {
            System.Drawing.Rectangle cropRect = new System.Drawing.Rectangle(x, y, width, height);
            System.Drawing.Bitmap target = new System.Drawing.Bitmap(cropRect.Width, cropRect.Height);

            using(System.Drawing.Graphics g = System.Drawing.Graphics.FromImage(target))
            {
                g.DrawImage(src, new System.Drawing.Rectangle(0, 0, target.Width, target.Height), cropRect, System.Drawing.GraphicsUnit.Pixel);
            }

            return target;
        }

        private System.Drawing.Bitmap CropImageBig(System.Drawing.Bitmap src, int x, int y, int width, int height)
        {
            System.Drawing.Rectangle cropRect = new System.Drawing.Rectangle(x, y, width, height);
            System.Drawing.Bitmap target = new System.Drawing.Bitmap(cropRect.Width*2, cropRect.Height*2);

            for (int k = 0; k < target.Width; k++)
            {
                for (int j = 0; j < target.Height; j++)
                {
                    target.SetPixel(k, j, System.Drawing.Color.White);
                }
            }

            using (System.Drawing.Graphics g = System.Drawing.Graphics.FromImage(target))
            {
                g.DrawImage(src, new System.Drawing.Rectangle(target.Width/2 - cropRect.Width/2, target.Height/2 - cropRect.Height/2, cropRect.Width, cropRect.Height), cropRect, System.Drawing.GraphicsUnit.Pixel);
            }

            return target;
        }

        private System.Drawing.Bitmap RotateCharacter(System.Drawing.Bitmap src, int Degrees)
        {
            int x;
            int y;
            double fTrueX;
            double fTrueY;
            double fDistance;
            double fPolarAngle;
            double fDeltaX;
            double fDeltaY;
            double fTopRed;
            double fBottomRed;
            int iRed;
            int iFloorX;
            int iFloorY;
            int iCeilingX;
            int iCeilingY;
            Color clrTopLeft;
            Color clrTopRight;
            Color clrBottomLeft;
            Color clrBottomRight;

            System.Drawing.Bitmap RotatedCharacter = new System.Drawing.Bitmap(src.Width, src.Height, src.PixelFormat);
            double Rad = Degrees * Math.PI / 180.0;


            for (int i = 0; i < src.Height; ++i)
            {
                for (int j = 0; j < src.Width; ++j)
                {
                    RotatedCharacter.SetPixel(j, i, Color.White);
                    // convert raster to Cartesian
                    x = j - src.Width / 2;
                    y = src.Height / 2 - i;

                    // convert Cartesian to polar
                    fDistance = Math.Sqrt(x * x + y * y);
                    fPolarAngle = 0.0;
                    if (x == 0)
                    {
                        if (y == 0)
                        {
                            // centre of image, no rotation needed
                            RotatedCharacter.SetPixel(j, i, src.GetPixel(j, i));
                            continue;
                        }
                        else if (y < 0)
                        {
                            fPolarAngle = 1.5 * Math.PI;
                        }
                        else
                        {
                            fPolarAngle = 0.5 * Math.PI;
                        }
                    }
                    else
                    {
                        fPolarAngle = Math.Atan2((double)y, (double)x);
                    }

                    //clockwise rotation
                    fPolarAngle -= Rad;

                    // convert polar to Cartesian
                    fTrueX = fDistance * Math.Cos(fPolarAngle);
                    fTrueY = fDistance * Math.Sin(fPolarAngle);

                    // convert Cartesian to raster
                    fTrueX = fTrueX + (double)src.Width/2;
                    fTrueY = (double)src.Height/2 - fTrueY;

                    iFloorX = (int)(Math.Floor(fTrueX));
                    iFloorY = (int)(Math.Floor(fTrueY));
                    iCeilingX = (int)(Math.Ceiling(fTrueX));
                    iCeilingY = (int)(Math.Ceiling(fTrueY));

                    // check bounds
                    if (iFloorX < 0 || iCeilingX < 0 || iFloorX >= src.Width || iCeilingX >= src.Width || iFloorY < 0 || iCeilingY < 0 || iFloorY >= src.Height || iCeilingY >= src.Height) continue;

                    fDeltaX = fTrueX - (double)iFloorX;
                    fDeltaY = fTrueY - (double)iFloorY;

                    clrTopLeft = src.GetPixel(iFloorX, iFloorY);
                    clrTopRight = src.GetPixel(iCeilingX, iFloorY);
                    clrBottomLeft = src.GetPixel(iFloorX, iCeilingY);
                    clrBottomRight = src.GetPixel(iCeilingX, iCeilingY);

                    // linearly interpolate horizontally between top neighbours
                    fTopRed = (1 - fDeltaX) * clrTopLeft.R + fDeltaX * clrTopRight.R;
                    // linearly interpolate horizontally between bottom neighbours
                    fBottomRed = (1 - fDeltaX) * clrBottomLeft.R + fDeltaX * clrBottomRight.R;
                    // linearly interpolate vertically between top and bottom interpolated results
                    iRed = (int)(Math.Round((1 - fDeltaY) * fTopRed + fDeltaY * fBottomRed));

                    // make sure colour values are valid
                    if (iRed < 0) iRed = 0;
                    if (iRed > 255) iRed = 255;

                    if (iRed > 128) iRed = 255;
                    if (iRed < 129) iRed = 0;

                    RotatedCharacter.SetPixel(j, i, Color.FromArgb(iRed, iRed, iRed));
                }
            }

            //Binarise(ref RotatedCharacter);

            return RotatedCharacter;
        }

        
        private int BoxSize(System.Drawing.Bitmap character)
        {
            Bitmap Box = new Bitmap(character);

            int x1;
            int x2;
            int y1;
            int y2;

            x1 = FindLetterStart(character, 0);
            x2 = FindLetterEnd(character, x1);
            y1 = FindLetterStartH(character, 0);
            y2 = FindLetterEndH(character, y1);

            /*
            DrawVerticalRedLine(ref Box, x1);
            DrawVerticalRedLine(ref Box, x2);
            DrawHorizontalRedLine(ref Box, y1);
            DrawHorizontalRedLine(ref Box, y2);
            */

            return ((x2 - x1) * (y2 - y1));
        }

        private int[] GetBoundingBox(Bitmap character)
        {
            int[] rect = new int[4];

            rect[0] = FindLetterStart(character, 0);
            rect[1] = FindLetterEnd(character, rect[0]);
            rect[2] = FindLetterStartH(character, 0);
            rect[3] = FindLetterEndH(character, rect[2]);

            return rect;
        }

        private Bitmap NormalizeRotationCharacter(Bitmap character)
        {
            Bitmap temp;

            int Degree = 0;
            int min = BoxSize(character);

            for (int i = -17; i < 0; i++)
            {
                temp = RotateCharacter(character, i);

                if(BoxSize(temp) < min)
                {
                    min = BoxSize(temp);
                    Degree = i;
                }
            }

            for (int i = 1; i <= 17; i++)
            {
                temp = RotateCharacter(character, i);

                if (BoxSize(temp) < min)
                {
                    min = BoxSize(temp);
                    Degree = i;
                }
            }

            return RotateCharacter(character, Degree);
        }

        private Bitmap NormalizeRectSize(Bitmap character)
        {
            int[] BoundBox = GetBoundingBox(character);
            System.Drawing.Rectangle cropRect = new System.Drawing.Rectangle(BoundBox[0], BoundBox[2], BoundBox[1] - BoundBox[0], BoundBox[3] - BoundBox[2]);
            System.Drawing.Bitmap target = new System.Drawing.Bitmap(40, 40);

            for (int k = 0; k < target.Width; k++)
            {
                for (int j = 0; j < target.Height; j++)
                {
                    target.SetPixel(k, j, System.Drawing.Color.White);
                }
            }

            using (System.Drawing.Graphics g = System.Drawing.Graphics.FromImage(target))
            {
                g.DrawImage(character, new System.Drawing.Rectangle(target.Width / 2 - cropRect.Width / 2, target.Height / 2 - cropRect.Height/2, cropRect.Width, cropRect.Height), cropRect, System.Drawing.GraphicsUnit.Pixel);
            }

            return target;
        }

        private bool IsInBounds(sPixel Pixel, int width, int height)
        {
            if (Pixel.x < 0 || Pixel.x >= width || Pixel.y < 0 || Pixel.y >= height)
            {
                return false;
            }
            else
            {
                return true;
            }
        }

        private void CleanUpSegmentedLetters(Bitmap character)
        {
            System.Collections.Generic.Queue<sPixel> queue = new System.Collections.Generic.Queue<sPixel>();
            int[,] visited = new int[character.Height, character.Width];
            System.Collections.Generic.List<object> Segments = new System.Collections.Generic.List<object>();

            bool outerBreak;
            sPixel CurrentPixel;
            sPixel Neighbor;


            while (true)
            {
                //init
                queue.Clear();
                outerBreak = false;
                for (int x = 0; x < character.Width && !outerBreak; x++)
                {
                    for (int y = 0; y < character.Height; y++)
                    {
                        Color Pixel = character.GetPixel(x, y);
                        if (Pixel.R == 0 && Pixel.G == 0 && Pixel.B == 0) //Black pixel
                        {
                            if (visited[x, y] == 0) //not visited yet
                            {
                                queue.Enqueue(new sPixel(x, y));
                                visited[x, y] = 1;
                                Segments.Add(new System.Collections.Generic.List<sPixel>());
                                outerBreak = true;
                                break;
                            }
                        }
                    }
                }

                if (queue.Count == 0)
                {
                    break;
                }

                while (queue.Count != 0)
                {
                    //look at neighbors
                    CurrentPixel = queue.Dequeue();

                    for (int i = -1; i < 2; i++)
                    {
                        for (int j = -1; j < 2; j++)
                        {
                            Neighbor.x = CurrentPixel.x - i;
                            Neighbor.y = CurrentPixel.y - j;

                            if (IsInBounds(Neighbor, character.Height, character.Width))
                            {
                                Color color = character.GetPixel(Neighbor.x, Neighbor.y);
                                if (color.R == 0 && color.G == 0 && color.B == 0)
                                {
                                    if (visited[Neighbor.x, Neighbor.y] == 0)
                                    {
                                        queue.Enqueue(new sPixel(Neighbor.x, Neighbor.y));
                                        ((System.Collections.Generic.List<sPixel>)(Segments[Segments.Count - 1])).Add(new sPixel(Neighbor.x, Neighbor.y));
                                        visited[Neighbor.x, Neighbor.y] = 1;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        private string DoGetRequest(string URL)
        {
            System.Net.HttpWebRequest request = (System.Net.HttpWebRequest)System.Net.HttpWebRequest.Create(URL);
            request.Credentials = System.Net.CredentialCache.DefaultCredentials;
            System.Net.HttpWebResponse response = (System.Net.HttpWebResponse)request.GetResponse();

            if (response.StatusCode == System.Net.HttpStatusCode.OK)
            {
                System.IO.Stream stream = response.GetResponseStream();
                System.IO.StreamReader reader = new System.IO.StreamReader(stream);
                string HTMLWebPage = reader.ReadToEnd();

                reader.Close();
                response.Close();

                return HTMLWebPage;
            }

            return null;
        }

        private Bitmap DoCaptchaRequest(string URL)
        {
            System.Net.HttpWebRequest request = (System.Net.HttpWebRequest)System.Net.HttpWebRequest.Create(URL);
            request.Credentials = System.Net.CredentialCache.DefaultCredentials;
            System.Net.HttpWebResponse response = (System.Net.HttpWebResponse)request.GetResponse();

            if (response.StatusCode == System.Net.HttpStatusCode.OK)
            {
                System.IO.Stream stream = response.GetResponseStream();
                Bitmap Captcha = new Bitmap(stream);

                response.Close();

                return Captcha;
            }

            return null;
        }

        private string ParseCaptchaURL(string HTMLRiot)
        {
            int index = HTMLRiot.IndexOf("CaptchaImg");
            int index2 = index;
            int index3;

            while (HTMLRiot.Substring(index2, 3) != "src")
            {
                index2--;
            }

            while (!HTMLRiot[index2].Equals('\"'))
            {
                index2++;
            }

            index3 = index2;
            index3++;

            while (!HTMLRiot[index3].Equals('\"'))
            {
                index3++;
            }

            string CaptchaURL = HTMLRiot.Substring(++index2, index3 - index2);

            return CaptchaURL;
        }
    }
